// Problem 8. Largest product in a series
// Find the thirteen adjacent digits in the 1000-digit number that have the greatest product. What is the value of this product?

function findLargestProductInSeries(num)
{
	// time: 0.008 seconds.
	
	var d0 = new Date();
	var testData = '';	
	var maxProduct = 0;
	var max_arr = [];
	
	for (var i = 0; i < testData.length; i++)
	{
		if(i < (testData.length - num))
		{
			var temp_product = 1;
			var temp_arr = [];
			for (var x = i; x < i+num; x++)
			{
				var number = Number(testData[x]);
				temp_arr.push(number);
				temp_product *= number;
			}
			if(maxProduct < temp_product)
			{
				maxProduct = temp_product;
				max_arr = temp_arr;
			}
			
		}
	}
	var d1 = new Date();
	console.log("Answer: " + maxProduct);
	console.log("time: " + (d1.getTime() - d0.getTime())/1000 + " seconds.");
}

findLargestProductInSeries(13);